package com.bee.algorithms.backtracing;import java.util.*;/** * 剑指 Offer II 079. 所有子集:https://leetcode.cn/problems/TVdhkn/ * * @author yangying * @version 1.0 * @since 2022/5/31 **/public class Subsets {    public List<List<Integer>> subsets(int[] nums) {        if(nums.length == 0){            return new ArrayList<>();        }        List<List<Integer>> result = new ArrayList<>();        LinkedList<Integer> subset = new LinkedList<>();        subsets(nums, 0, subset, result);        return result;    }    private void subsets(int[] nums, int i, LinkedList<Integer> subset, List<List<Integer>> result) {        if (i == nums.length) {            result.add(new LinkedList<>(subset));        } else{            subsets(nums, i + 1, subset, result);            subset.add(nums[i]);            subsets(nums, i + 1, subset, result);            subset.removeLast();        }    }}