package com.bee.algorithms.backtracing.others;import java.util.ArrayList;import java.util.List;/** * 剑指 Offer II 085. 生成匹配的括号：https://leetcode.cn/problems/IDBivT/ * * @author yangying * @version 1.0 * @since 2022/6/1 **/public class GenerateParenthesis {    public List<String> generateParenthesis(int n) {        List<String> result = new ArrayList<>();        generateParenthesis(n,0,0,"",result);        return result;    }    private void generateParenthesis(int n,int index,int sum,String str,List<String> result){        if(sum ==0 &&index == n*2){            result.add(str) ;        }else if(sum>=0 && index<n*2){            sum += 1;            str +="(";            generateParenthesis(n,index+1,sum,str,result);            sum -= 1;            str = str.substring(0,str.length()-1);            sum += -1;            str +=")";            generateParenthesis(n,index+1,sum,str,result);            sum -= -1;            str = str.substring(0,str.length()-1);        }    }}