package com.bee.algorithms.dynamicprogramming.bag;/** * 剑指 Offer II 104. 排列的数目 * * @author yangying * @version 1.0 * @since 2022/6/8 **/public class CombinationSum4 {    public int combinationSum4(int[] nums, int target) {        int[] dp = new int[target+1];        dp[0] = 1;        for(int i=1;i<=nums.length;i++){            for(int num:nums){                if(i>=num){                    dp[i] += dp[i-num];                }            }        }        return dp[target];    }}