package com.bee.algorithms.dynamicprogramming.singleseq;/** * 剑指 Offer II 090. 环形房屋偷盗:https://leetcode.cn/problems/PzWKhm/ * * @author yangying * @version 1.0 * @since 2022/6/2 **/public class RobII {    public int rob(int[] nums) {        int n = nums.length;        int[] dp = new int[n+1];        dp[0] = 0;        dp[1] = nums[1];        for(int i=2;i<=n;i++){            if(i == n && n%2==1){               dp[i] = Math.max(dp[i-1],dp[i-2]);            }            dp[i] =Math.max(dp[i-2],dp[i-1]+nums[i-1]);        }        return dp[n-1];    }}