package com.bee.algorithms.search.binary;/** * 剑指 Offer II 069. 山峰数组的顶部：https://leetcode.cn/problems/B1IidL/ * * @author yangying * @version 1.0 * @since 2022/5/29 **/public class PeakIndexInMountainArray {    public int peakIndexInMountainArray(int[] arr) {        int left = 1;        int right = arr.length - 2;        int ans = 0;        while (left <= right) {            ans = (left + right) / 2;            int mid = (left + right) / 2;            if (arr[mid] > arr[mid + 1]) {                ans = mid;                right = mid - 1;            } else {                left = mid + 1;            }        }        return ans;    }}