package com.bee.algorithms.search.binary;/** * 剑指 Offer II 068. 查找插入位置:https://leetcode.cn/problems/N6YdxV/ * * @author yangying * @version 1.0 * @since 2022/5/29 **/public class SearchInsert {    public int searchInsert(int[] nums, int target) {        int left = 0;        int right = nums.length - 1;        while (left <= right) {            int mid = (left + right) / 2;            if (nums[mid] == target) {                return mid;            }            if (nums[mid] > target) {                right = mid-1;            }            if (nums[mid] < target) {                left = mid+1;            }        }        return left;    }}