package com.bee.algstruct.algorithms.backtracing.others;import java.util.ArrayList;import java.util.List;/** * 剑指 Offer II 087. 复原 IP :https://leetcode.cn/problems/0on3uN/ * * @author yangying * @version 1.0 * @since 2022/6/1 **/public class RestoreIpAddresses {    public List<String> restoreIpAddresses(String s) {        StringBuilder ip = new StringBuilder();        List<String> ips = new ArrayList<>();        restoreIpAddresses(s,0,4,ip,ips);        return ips;    }    private void restoreIpAddresses(String s,int index,int segment,StringBuilder ip,List<String> ips){        if(index == s.length()){            ips.add(ip.toString());            return;        }        for(int i = index;i<ip.length();i++){            if(isValid(s,index,i)&&segment>=0){                ip.append(".").append(s.substring(index,i+1));                restoreIpAddresses(s,index+1,segment-1,ip,ips);                ip.delete(ip.length()-i+index,ip.length()-1);            }        }    }    private boolean isValid(String s,int start,int end){        String str = s.substring(start,end);        if(s.startsWith("0")){            return false;        }        int num = Integer.parseInt(str);        return num > 0 && num <= 255;    }}