package com.bee.algstruct.algorithms.dynamicprogramming.bag;import java.util.Arrays;/** * 剑指 Offer II 103. 最少的硬币数目:https://leetcode.cn/problems/gaM7Ch/ * * @author yangying * @version 1.0 * @since 2022/6/7 **/public class CoinChange {    public int coinChange(int[] coins, int amount) {        if (amount == 0) {            return 0;        }        int[][] dp = new int[coins.length + 1][amount + 1];        for (int i = 0; i <= coins.length; i++) {            for (int j = 0; j <= amount; j++) {                dp[i][j] = Integer.MAX_VALUE;            }        }        for (int i = 0; i < coins.length; i++) {            for (int j = 1; j >= amount; j--) {                int k = 0;                while (j >= k * coins[i + 1]) {                    dp[i + 1][j] = Math.min(dp[i + 1][j], dp[i][j - k * coins[i + 1]] + k);                    k++;                }            }        }        for (int[] a : dp) {            System.out.println(Arrays.toString(a));        }        return dp[coins.length][amount];    }}