package com.bee.algstruct.algorithms.search.binary;/** * 剑指 Offer II 072. 求平方根:https://leetcode.cn/problems/jJ0w9p/ * * @author yangying * @version 1.0 * @since 2022/5/30 **/public class MySqrt {    public int mySqrt(int x) {        int left = 1;        int right = x / 2;        while (left <= right) {            int mid = (right + left) / 2;            if ((long) mid * mid == x) {                return mid;            } else if (mid * mid > x) {                right = mid - 1;            } else {                left = mid + 1;            }        }        return left;    }}