package com.bee.algstruct.datastruct.queue;import java.util.LinkedList;import java.util.Queue;/** * 剑指 Offer II 041. 滑动窗口的平均值:https://leetcode.cn/problems/qIsx9U/ * * @author yangying * @version 1.0 * @since 2022/5/25 **/public class MovingAverage {    private Queue<Integer> queue;    private int size;    private double sum = 0;    /** Initialize your data structure here. */    public MovingAverage(int size) {        queue = new LinkedList<>();        this.size = size;    }    public double next(int val) {        while (queue.size() == size){            sum -= queue.poll();        }        sum += val;        queue.add(val);        return sum/queue.size();    }}/** * Your MovingAverage object will be instantiated and called as such: * MovingAverage obj = new MovingAverage(size); * double param_1 = obj.next(val); */