package com.bee.algstruct.datastruct.tree;/** * TODO:class description * * @author yangying * @version 1.0 * @since 2022/5/25 **/public class TreeNode {    public int val;    public TreeNode left;    public TreeNode right;    public TreeNode() {    }    public TreeNode(int val) {        this.val = val;    }    public TreeNode(int val, TreeNode left, TreeNode right) {        this.val = val;        this.left = left;        this.right = right;    }}