package com.bee.algstruct.datastruct.tree.binarysearchtree;import java.util.TreeSet;/** * 剑指 Offer II 057. 值和下标之差都在给定的范围内：https://leetcode.cn/problems/7WqeDu/ * * @author yangying * @version 1.0 * @since 2022/5/28 **/public class ContainsNearbyAlmostDuplicate {    public boolean containsNearbyAlmostDuplicate(int[] nums, int k, int t) {        int n = nums.length;        TreeSet<Long> set = new TreeSet<>();        for (int i = 0; i < n; i++) {            Long ceiling = set.ceiling(Math.abs((long) t - (long) nums[i]));            if (ceiling != null && ceiling <= (long) nums[i] + (long) t) {                return true;            }            set.add((long) nums[i]);            if (i >= k) {                set.remove((long) nums[i]);            }        }        return false;    }}