package com.bee.algstruct.datastruct.tree.binarysearchtree;import com.bee.algstruct.datastruct.tree.TreeNode;/** * 剑指 Offer II 054. 所有大于等于节点的值之和:https://leetcode.cn/problems/w6cpku/ * * @author yangying * @version 1.0 * @since 2022/5/28 **/public class ConvertBST {    int sum = 0;    public TreeNode convertBST(TreeNode root) {         if(root != null){              convertBST(root.right);              sum += root.val;              root.val = sum;              convertBST(root.left);         }        return  root;    }}