package com.bee.algstruct.datastruct.tree.binarysearchtree;import com.bee.algstruct.datastruct.tree.TreeNode;import java.util.HashSet;import java.util.Set;/** * 剑指 Offer II 056. 二叉搜索树中两个节点之和:https://leetcode.cn/problems/opLdQZ/ * * @author yangying * @version 1.0 * @since 2022/5/28 **/public class FindTarget {    Set<Integer> set = new HashSet<>();    public boolean findTarget(TreeNode root, int k) {        if (root == null) {            return false;        }        if (set.contains(k - root.val)) {            return true;        }        set.add(root.val);        return findTarget(root.left, k) || findTarget(root.right, k);    }}