package com.bee.algstruct.datastruct.tree.binarytreedfs;import com.bee.algstruct.datastruct.tree.TreeNode;/** * 剑指 Offer II 047. 二叉树剪枝：https://leetcode.cn/problems/pOCWxh/ * * @author yangying * @version 1.0 * @since 2022/5/25 **/public class PruneTree {    public TreeNode pruneTree(TreeNode root) {        if (root == null) {            return null;        }        root.left = pruneTree(root.left);        root.right = pruneTree(root.right);        if (root.left == null && root.right == null && root.val == 0) {            return null;        }        return root;    }    private boolean isZeroTree(TreeNode node) {        boolean isZeroTree = true;        if (node == null) {            return true;        }        if (node.val == 1) {            isZeroTree = false;        }        if (isZeroTree(node.left)) {            node.left = null;        } else {            isZeroTree = false;        }        if (isZeroTree(node.right)) {            node.right = null;        } else {            isZeroTree = false;        }        return isZeroTree;    }}