package com.bee.algstruct.datastruct.tree.binarytreedfs;import com.bee.algstruct.datastruct.tree.TreeNode;/** * 剑指 Offer II 049. 从根节点到叶节点的路径数字之和:https://leetcode.cn/problems/3Etpl5/ * * @author yangying * @version 1.0 * @since 2022/5/26 **/public class SumNumbers {    public int sumNumbers(TreeNode root) {        return dfs(root,0);    }    private int dfs(TreeNode root, int path) {        if (root == null) {            return 0;        }        path = path * 10 + root.val;        if (root.left == null && root.right == null) {            return path;        }        return dfs(root.left, path) + dfs(root.right, path);    }}