package com.bee.datastruct.queue;import java.util.LinkedList;import java.util.Queue;/** * 剑指 Offer II 042. 最近请求次数:https://leetcode.cn/problems/H8086Q/ * * @author yangying * @version 1.0 * @since 2022/5/25 **/public class RecentCounter {    Queue<Integer> requests = new LinkedList<>();    public RecentCounter() {    }    public int ping(int t) {        while (!requests.isEmpty() && t - requests.peek()>3000){            requests.poll();        }        requests.add(t);        return requests.size();    }}/** * Your RecentCounter object will be instantiated and called as such: * RecentCounter obj = new RecentCounter(); * int param_1 = obj.ping(t); */