package com.bee.datastruct.queue.binraytreebfs;/** * TreeNode * * @author yangying * @version 1.0 * @since 2022/5/25 **/class TreeNode {    int val;    TreeNode left;    TreeNode right;    TreeNode() {    }    TreeNode(int val) {        this.val = val;    }    TreeNode(int val, TreeNode left, TreeNode right) {        this.val = val;        this.left = left;        this.right = right;    }}