package com.bee.network.timeservice.bio;import java.io.IOException;import java.net.ServerSocket;import java.net.Socket;import java.util.concurrent.ExecutorService;import java.util.concurrent.Executors;/** * 服务端启动程序 * * @author yangying * @version 1.0 * @since 2022/7/11 **/public class ServerStarter {    private final static int PORT =8080;    public static void main(String[] args) {        ServerSocket serverSocket = null;        ExecutorService pool = Executors.newFixedThreadPool(20);        try {            serverSocket = new ServerSocket(PORT);            System.out.println("server started,listening for request.");            while (true) {                Socket socket = serverSocket.accept();                System.out.println("revice new connection");                if (socket != null) {                    pool.submit(new TimerServer(socket));                }            }        } catch (IOException e) {            e.printStackTrace();            throw new RuntimeException(e);        }finally {            if(serverSocket != null){                try {                    serverSocket.close();                } catch (IOException e) {                    throw new RuntimeException(e);                }                ;                serverSocket = null;            }        }    }}